package JavaConcepts;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// DataTypes: int, double, char, boolean
	   // Int data type : store the integer value 
		  int i=2;
		  int j=3;
		  int k=-4;
		System.out.println(i);
		System.out.println(i+j);
		
		// double data types 
		   double f= 5.66;
		   double f1=6.77;
		   System.out.println(f1);
		   System.out.println(f+f1);
		   System.out.println("You got the idea about java data types");
		   
		 // char : Only single digit value , should be written within the single quotes.
		   char a='1';
		   char b='2';
		   char c='3';
		   System.out.println(a);
		   System.out.println(a*b);
		   
		   // boolean is true or false statement 
		   
		   boolean bo=true;
		   boolean bo1=false;
		   System.out.println(bo1);
		   
		   System.out.println("Increment operation ");
		   
		 //  Increment operators 
		   int D=50;
		   D++;
		   System.out.println(D);
		   D+=5;
		   System.out.println(D);
		   
		   System.out.println("Decrement operators");
		   
		 // Decrement operators
		   D--;
		   System.out.println(D);
		   D-=5;
		   System.out.println(D);
				   
		   
		   		
		
	}

}
