package LoopsConcept;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

   for(int i=0;i<=10;i++) {
	   
	   System.out.println(i);
   }

   System.out.println("Reverse order");
   for(int i=10;i>=1;i--) {
	   System.out.println(i);
   }
  }

}
